mapper.select:
select * from product


    and id = #{id}


     and product_name like concat('%',#{productName},'%')


    and car_id = #{carId}

mapper.insert:
insert into product (product_name, car_id, price, 
  original_quantity, color, purchase_price, 
  min_quantity, max_quantity, car_desc, 
  model_preview_url, update_time, create_time
  )
values (#{productName,jdbcType=VARCHAR}, #{carId,jdbcType=INTEGER}, #{price,jdbcType=DECIMAL}, 
  #{originalQuantity,jdbcType=INTEGER}, #{color,jdbcType=VARCHAR}, #{purchasePrice,jdbcType=DECIMAL}, 
  #{minQuantity,jdbcType=INTEGER}, #{maxQuantity,jdbcType=INTEGER}, #{carDesc,jdbcType=VARCHAR}, 
  #{modelPreviewUrl,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}
  )
